import {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css'


import Movie from './Components/Movie.js'
import Add from './Components/Add.js'
import Edit from './Components//Edit.js'


function App() {

  //  const [display, setDisplay] = useState(false)
   const [movies, setMovies] = useState([])

  //Get...Calling on Movie.js file
  const getMovies = () => {
     axios.get('http://localhost:3000/movierec')
     .then((response) => setMovies(response.data), (err) => console.log(err))
     .catch((error) => console.log(error))
  }


  //Create...Calling on Add.js file
  const handleCreate = (data) => {
    console.log(data)
     axios.post('http://localhost:3000/movierec', data)
     .then((response) => {
        setMovies([...movies, response.data])

     })
  }

  //Edit
  const handleEdit = (data) => {
    axios.put('http://localhost:3000/movierec/' + data._id, data)
    .then((response) => {
       let newMovies = movies.map((movies) => {
        return movies._id !== data._id ? movies : data
       })
       setMovies(newMovies)
    })
 }

 //Delete
  const handleDelete = (deletedMovies) => {
     axios.delete('http://localhost:3000/movierec/' + deletedMovies._id)
     .then((response) => {
      let newMovies = movies.filter((movies) => {
        return movies._id !== response._id
       })
      setMovies(newMovies)
      getMovies()
     })
  }

  useEffect(() => {
     getMovies()
  }, [])

  return (
    <>
      <div className="header">
      <header>
      <img className="backgroundimg" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ5OCusHEe21ILJPmV022KgtsowWlqOblv60Q&usqp=CAU"></img>
      <h3 className="titleMR">Movie Recommendation</h3>
      </header>
      </div>
      <div className="container">
      {/* <button onClick={() => setDisplay(!display)}>Add New Movie</button> */}

      <Add handleCreate = {handleCreate}/> 

      {movies.map((movie) => {
        return (
          <div>
            <Movie movie = {movie}/>
            <Edit movie = {movie} handleEdit = {handleEdit}/>
            <button onClick={()=> {handleDelete(movie)}}>Delete</button>
          </div>
        )
      })}
      </div>
      <div id="carouselExampleFade" className="carousel slide carousel-fade $carousel-control-transition:opacity .5s ease mw-100" data-bs-ride="carousel">
  <div className="carousel-inner">
    <div className="carousel-item active">
      <img src="https://static.foxbusiness.com/foxbusiness.com/content/uploads/2021/11/Elf-on-the-Shelf-49ScoutElfKazooBand-thumbnail.png" className="d-block w-100" alt="..."/>
    </div>
    <div className="carousel-item">
      <img src="https://m.media-amazon.com/images/M/MV5BMTk4MDM0MDM3N15BMl5BanBnXkFtZTgwMzIzMzU2MzE@._V1_.jpg" className="d-block w-100" alt="..."/>
    </div>
    <div className="carousel-item">
      <img src="https://h2.commercev3.net/cdn.bitsandpieces.com/images/1000/40481.jpg" className="d-block w-100" alt="..."/>
    </div>
  </div>
  <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="prev">
    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
    <span className="visually-hidden">Previous</span>
  </button>
  <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleFade" data-bs-slide="next">
    <span className="carousel-control-next-icon" aria-hidden="true"></span>
    <span className="visually-hidden">Next</span>
  </button>
</div>
    </>
  );
}

export default App;
